import styled, { css } from "styled-components";
import React, { useState } from 'react';
import Dropzone from 'react-dropzone'
import Logo from "../assets/Logotipo_do_Supremo_Tribunal_Federal.svg";

function Pages() {
    const [file, setFile] = useState(null);

    const handleUpload = (acceptedFiles) => {
        console.log("logging drop/selected file", acceptedFiles);
        // fake request to upload file
        const url = "https://api.escuelajs.co/api/v1/files/upload";
        const formData = new FormData();
        formData.append("file", acceptedFiles[0]); // Assuming you only accept one file

        fetch(url, {
            method: "POST",
            body: formData,
        })
            .then((response) => {
                if (response.ok) {
                    // File uploaded successfully
                    setFile(acceptedFiles[0]);
                } else {
                    // File upload failed
                    console.error(response);
                }
            })
            .catch((error) => {
                console.error(error);
            });
    };

    return (
        <div>
            <FormContainer>
                <form>
                    <div className="brand">
                        <img src={Logo} alt="Logo" />
                    </div>
                    <Dropzone onDrop={handleUpload} accept="image/*" minSize={1024} maxSize={3072000}>
                        {({ getRootProps, getInputProps, isDragActive, isDragAccept, isDragReject }) => {
                            const additionalClass = isDragAccept ? "accept" : isDragReject ? "reject" : "";

                            return (
                                <div
                                    {...getRootProps({
                                        className: `dropzone ${additionalClass}`,
                                    })}
                                >
                                    <input {...getInputProps()} />
                                    <p>Drag'n'drop images, or click to select files</p>
                                </div>
                            );
                        }}
                    </Dropzone>
                    {file && (
                        <>
                            <h4>File Uploaded Successfully !!</h4>
                            <img src={URL.createObjectURL(file)} className="img-container" alt="Uploaded file" />
                        </>
                    )}
                </form>
            </FormContainer>
        </div >
    );
}

const FormContainer = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  align-items: center;
  background-color: #131324;

span {
  font-size: 2rem;
  color: #777;
}

.dropzone {
  text-align: center;
  padding: 20px;
  border: 3px dashed #eeeeee;
  background-color: #fafafa;
  color: #bdbdbd;
  margin-bottom: 20px;
}

.accept {
  border-color: #107c10 !important;
}

.reject {
  border-color: #d83b01 !important;
}

.img-container {
  width: 639px;
  height: 500px;
}
  .brand {
    display: flex;
    align-items: center;
    gap: 1rem;
    justify-content: center;

    img {
      height: 5rem;
    }
  }
 
  form {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    background-color: #00000076;
    border-radius: 2rem;
    padding: 3rem 5rem;
  }

  /* Existing styles for input, button, and span */
`;

export default Pages;
