import styled, { css } from "styled-components";
import React from 'react';
import Dropzone from 'react-dropzone'
import Logo from "../assets/Logotipo_do_Supremo_Tribunal_Federal.svg";

function Pages() {

    const handleUpload = (acceptedFiles) => {
    }
    const renderDragMessage = (isDragActive, isDragReject) => {
        if (!isDragActive) {
            return <UploadMessage>Arraste arquivos aqui...</UploadMessage>;
        }

        if (isDragReject) {
            return <UploadMessage type="error">Arquivo não suportado</UploadMessage>;
        }

        return <UploadMessage type="success">Solte os arquivos aqui</UploadMessage>;
    }

    return (
        <div>
            <FormContainer>
                <form>
                    <div className="brand">
                        <img src={Logo} alt="Logo" />
                    </div>
                    <Dropzone onDrop={handleUpload} accept="image/*" minSize={1024} maxSize={3072000}>
                        {({ getRootProps, getInputProps, isDragActive, isDragAccept, isDragReject }) => {
                            const additionalClass = isDragAccept ? "accept" : isDragReject ? "reject" : "";

                            return (
                                <div
                                    {...getRootProps({
                                        className: `dropzone ${additionalClass}`,
                                    })}
                                >
                                    <input {...getInputProps()} />
                                    <p>Drag'n'drop images, or click to select files</p>
                                </div>
                            );
                        }}
                    </Dropzone>
                    {/* <Dropzone accept="image/*">
                        {({ getRootProps, getInputProps, isDragActive, isDragReject }) => (
                            <DropContainer
                                {...getRootProps()}
                                isDragActive={isDragActive}
                                isDragReject={isDragReject}
                            >
                                <input {...getInputProps()} />
                                {renderDragMessage(isDragActive, isDragReject)}
                            </DropContainer>
                        )}
                    </Dropzone> */}
                </form>
            </FormContainer>
        </div >
    );
}


const messageColors = {
    default: "#999",
    error: "#e57878",
    success: "#78e5d5"
};

const UploadMessage = styled.p`
    display: flex;
    color: ${props => messageColors[props.type || "default"]};
    justify-content: center;
    align-items: center;
    padding: 15px 0;
  `;

const dragActive = css`
  border-color: #78e5d5;
`;

const dragReject = css`
  border-color: #e57878;
`;
const dropzone = css`
    border: 1px dashed #ddd;
    border-radius: 4px;
    cursor: pointer;
  
    transition: height 0.2s ease;
  
    ${props => props.isDragActive && dragActive};
    ${props => props.isDragReject && dragReject};
  `;

const FormContainer = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  align-items: center;
  background-color: #131324;
  .dropzone{  border: 1px dashed #ddd;
    border-radius: 4px;
    cursor: pointer;
  
    transition: height 0.2s ease;
  
    ${props => props.isDragActive && dragActive};
    ${props => props.isDragReject && dragReject};
    }
  .brand {
    display: flex;
    align-items: center;
    gap: 1rem;
    justify-content: center;

    img {
      height: 5rem;
    }
  }
 
  form {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    background-color: #00000076;
    border-radius: 2rem;
    padding: 3rem 5rem;
  }

  /* Existing styles for input, button, and span */
`;

export default Pages;
