import styled from "styled-components";
import React, { useState } from 'react';
import Logo from "../assets/Logotipo_do_Supremo_Tribunal_Federal.svg";

function Pages() {
    const [folderPath, setFolderPath] = useState('');

    const handleSelectFolder = async () => {
        const result = await window.electron.openFolderDialog();
        if (result && result.length > 0) {
            setFolderPath(result[0]);
            console.log(result[0])
        }
    };

    return (
        <div>
            <FormContainer>
                <form>
                    <div className="brand">
                        <img src={Logo} alt="Logo" />
                    </div>
                    <button onClick={handleSelectFolder}>Selecionar Pasta</button>
                    {folderPath && <p>Caminho da pasta selecionada: {folderPath}</p>}
                    <button type="submit">BAIXAR</button>
                </form>
            </FormContainer>
        </div>
    );
}

const FormContainer = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  align-items: center;
  background-color: #131324;

  .brand {
    display: flex;
    align-items: center;
    gap: 1rem;
    justify-content: center;

    img {
      height: 5rem;
    }
  }
 
  form {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    background-color: #00000076;
    border-radius: 2rem;
    padding: 3rem 5rem;
  }

  /* Existing styles for input, button, and span */
`;

export default Pages;
