import styled from "styled-components";
import React, { useState } from 'react';
import Logo from "../assets/Logotipo_do_Supremo_Tribunal_Federal.svg";

function Pages() {
    const [filePath, setFilePath] = useState('');

    const handleFileChange = (event) => {
        event.preventDefault(); // Impede a ação padrão de carregar arquivos

        const files = event.target.files;
        if (files.length > 0) {
            const path = files[0].webkitRelativePath;
            const folderName = path.split('/')[0];
            setFilePath(folderName);
            console.log(folderName)
        }
    };

    return (
        <div>
            <FormContainer>
                <form>
                    <div className="brand">
                        <img src={Logo} alt="Logo" />
                    </div>
                    <button type="submit">BAIXAR ÚLTIMO</button>
                    <input type="file" webkitdirectory="true" onChange={handleFileChange} />
                    <p>Selected File Path: {filePath}</p>
                </form>
            </FormContainer>
        </div>
    );
}

const FormContainer = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  align-items: center;
  background-color: #131324;

  .brand {
    display: flex;
    align-items: center;
    gap: 1rem;
    justify-content: center;

    img {
      height: 5rem;
    }
  }
 
  form {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    background-color: #00000076;
    border-radius: 2rem;
    padding: 3rem 5rem;
  }

  /* Existing styles for input, button, and span */
`;

export default Pages;
