import styled from "styled-components";
import React, { useRef } from "styled-components";

import Logo from "../assets/Logotipo_do_Supremo_Tribunal_Federal.svg";

function Pages() {
    const fileInputRef = useRef(null);

    const handleSelectFolder = () => {
        fileInputRef.current.click();
    };

    const handleFolderChange = (event) => {
        const selectedFolder = event.target.files[0].webkitPath;
        console.log("Pasta selecionada:", selectedFolder);
        // Store the folder path using localStorage, Redux, or your preferred method.
    };

    return (
        <div>
            <FormContainer>
                <form>
                    <div className="brand">
                        <img src={Logo} alt="Logo" />
                    </div>
                    <button type="button" onClick={handleSelectFolder}>
                        SELECIONAR PASTA
                    </button>
                    <input
                        type="file"
                        webkitdirectory
                        directory
                        ref={fileInputRef}
                        onChange={handleFolderChange}
                        style={{ display: "none" }} // Hide the input visually
                    />
                </form>
            </FormContainer>
        </div>
    );
}

const FormContainer = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  align-items: center;
  background-color: #131324;

  .brand {
    display: flex;
    align-items: center;
    gap: 1rem;
    justify-content: center;

    img {
      height: 5rem;
    }
  }
 
  form {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    background-color: #00000076;
    border-radius: 2rem;
    padding: 3rem 5rem;
  }

  /* Existing styles for input, button, and span */
`;

export default Pages;
