import styled from "styled-components";
import React, { useState, useRef } from 'react';
import Logo from "../assets/Logotipo_do_Supremo_Tribunal_Federal.svg";
import { CopyToClipboard } from 'react-copy-to-clipboard';

function Pages() {
    const [filePath, setFilePath] = useState('');
    const fileInputRef = useRef(null);

    const handleButtonClick = () => {
        fileInputRef.current.click();
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            console.log(file.path || file.name)
            setFilePath(file.path || file.name); // file.path funciona em Electron, file.name para navegadores
        }
    };

    return (
        <div>
            <FormContainer>
                <form>
                    <div className="brand">
                        <img src={Logo} alt="Logo" />
                    </div>
                    <button onClick={handleButtonClick}>Selecionar Arquivo</button>
                    <input
                        type="file"
                        ref={fileInputRef}
                        style={{ display: 'none' }}
                        onChange={handleFileChange}
                    />
                    {filePath && (
                        <div>
                            <p>File path: {filePath}</p>
                            <CopyToClipboard text={filePath}>
                                <button>Copy File Path</button>
                            </CopyToClipboard>
                        </div>
                    )}
                </form>
            </FormContainer>
        </div>
    );
}

const FormContainer = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  align-items: center;
  background-color: #131324;

  .brand {
    display: flex;
    align-items: center;
    gap: 1rem;
    justify-content: center;

    img {
      height: 5rem;
    }
  }
 
  form {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    background-color: #00000076;
    border-radius: 2rem;
    padding: 3rem 5rem;
  }

  /* Existing styles for input, button, and span */
`;

export default Pages;
